import React from 'react';
import './App.css';
const axios = require('axios');

const testData = [
  {name: "Dan Abramov", avatar_url: "https://avatars0.githubusercontent.com/u/810438?v=4", company: "@facebook"},
  {name: "Latori Miller", avatar_url: "https://avatars0.githubusercontent.com/u/16719162?v=4", company: "Road to Hire"},
  {name: "Dylan Trimble", avatar_url: "https://avatars1.githubusercontent.com/u/42077977?v=4", company: "Road to Hire"},
];
class Form extends React.Component {
  userNameInput = React.createRef();

  handleSubmit = async (event) => {
    event.preventDefault();
    const resp = await axios.get(`https://api.github.com/users/
    ${this.userInput.current.value}`);
    console.log('submitted');
    console.log(this.userInput.current.value);
    console.log(resp);
  }
  render(){
    userNameInput = React.createRef();
    return (
      <form onSubmit={this.handleSubmit}>
        <input type="text" placeholder="Github username" ref={this.userNameInput} required/>
        <button>Add card</button>
      </form>
    );
  }
}

const CardList = (props) => (
  <div>
   {props.profiles.map(profile =><Card {...profile}/>)}
  </div>
)

class App extends React.Component {
  constructor(props){
    super(props);
      this.state = {
        profiles: testData
      };
  } 

  render() {
    return (
      <div>
        <div className="header">{this.props.title}</div>
        <Form />
        <CardList profiles={this.state.profiles} />
        <ConditionalStyle />
      </div>
    )
  }
}

class ConditionalStyle extends React.Component{
  render() {
    return (
      <div style={{ color: Math.random() < 0.5? 'green' : 'red'}}
      >Look at me. I change colors.</div>
    )
  }
}

class Card extends React.Component {
  render() {
    const profile = this.props;
    return (
      <div className="github-profile">
        <img src={"profile.avatar_url"} />
        <img src="https://placehold.it/75" />
        <div className="info">
          <div className="name">{profile.name}</div>
          <div className="company">{profile.company}</div>
        </div>
      </div>
    );
  }
}

